import { Component, OnInit } from '@angular/core';
import { WelcomePageServiceService } from 'src/app/services/WelcomePage/welcome-page-service.service';

@Component({
  selector: 'app-welcome-page',
  templateUrl: './welcome-page.component.html',
  styleUrls: []
})
export class WelcomePageComponent implements OnInit {

  public MessageWelcome : any;

  constructor(private WelcomeService : WelcomePageServiceService){}

  ngOnInit(): void {

     this.WelcomeService.getHome("Home").subscribe(
      {
        next: (value) => {this.MessageWelcome = value},
        error:(error) =>{window.alert(error.message)}
        }

     )            
  }

}


<h3>{{MessageWelcome}}</h3>

<app-menu-main></app-menu-main>

<img src="https://conceptodefinicion.de/wp-content/uploads/2019/08/Medicina-.jpg">
