import { Component, OnInit } from '@angular/core';
import { WelcomePageServiceService } from 'src/app/services/welcome-page-service.service';

@Component({
  selector: 'app-welcome-page',
  templateUrl: './welcome-page.component.html',
  styleUrls: []
})
export class WelcomePageComponent implements OnInit {

  public msg : any;
  public MessageWelcome : String = "";

  constructor(private WelcomeService : WelcomePageServiceService){}

  ngOnInit(): void {

     this.msg = this.WelcomeService.getHome().subscribe(
      {
        next(value) {
            console.log(value.valueOf())

        },error(err) {
            console.log(err.message)
            window.alert(err.message)
        },complete() {
            console.log('complete')

        },
      })

      this.MessageWelcome = this.msg;




  }
}
